    <div class="text-white">    
        <section id="home" class="d-flex align-items-center justify-content-center text-center" style="background: url('https://source.unsplash.com/random/800x600?stars') center/cover;">
            <div class="p-4 bg-primary bg-opacity-50 rounded">
                <div class="upper pb-15">
                    <h1 style="">Astronomiyani O'rganing!</h1>
                </div>
                
                <p class="lead mb-4 text-white">Yulduzlar va koinot sirlarini kashf eting</p>
                <a href="{{ route('login') }}" class="btn btn-danger">Kurslarni Ko'rish</a>
                <div style="width: 100%" class="mt-3">
                    <iframe width="90%" height="10%" style="border: 0px" allowfullscreen="true" srcdoc="
                                    <!DOCTYPE html>
                                        <html>
                                          <head>
                                            <title>A simple Three.JS App</title>
                                            <link rel='stylesheet' href='threejs/style.css' />
                                          </head>
                                          <body>
                                            <main>
                                              <div id='container3D'></div>
                                            </main>
                                            <script type='module'>
                                                import * as THREE from 'https://cdn.skypack.dev/three@0.129.0/build/three.module.js';
                                                import { OrbitControls } from 'https://cdn.skypack.dev/three@0.129.0/examples/jsm/controls/OrbitControls.js';
                                                import { GLTFLoader } from 'https://cdn.skypack.dev/three@0.129.0/examples/jsm/loaders/GLTFLoader.js';
        
                                                const scene = new THREE.Scene();
                                                const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
        
                                                let mouseX = window.innerWidth / 2;
                                                let mouseY = window.innerHeight / 2;
        
                                                let object;
                                                let controls;
                                                let objToRender = 'earth';
        
                                                const loader = new GLTFLoader();
        
                                                loader.load(
                                                `./models/${objToRender}/scene.glb`,
                                                function (gltf) {
                                                object = gltf.scene;
                                                object.traverse((child) => {
                                                if (child.isMesh) {
                                                child.castShadow = true;
                                                child.receiveShadow = true;
                                                }
                                                });
                                                scene.add(object);
        
                                                const boundingBox = new THREE.Box3().setFromObject(object);
                                                const size = new THREE.Vector3();
                                                boundingBox.getSize(size);
        
                                                const maxDimension = Math.max(size.x, size.y, size.z);
                                                const distance = maxDimension * 2;
                                                camera.position.set(10, 15, 10);
        
                                                const center = new THREE.Vector3();
                                                boundingBox.getCenter(center);
                                                camera.lookAt(0,0,0);
        
                                                controls.target.copy(center);
                                                controls.update();
        
                                                if (gltf.animations && gltf.animations.length > 0) {
                                                const mixer = new THREE.AnimationMixer(object);
                                                gltf.animations.forEach((clip) => {
                                                const action = mixer.clipAction(clip);
                                                action.play();
                                                });
                                                object.mixer = mixer;
                                                }
                                                },
                                                function (xhr) {
                                                console.log((xhr.loaded / xhr.total) * 100 + '% loaded');
                                                },
                                                function (error) {
                                                console.error(error);
                                                }
                                                );
        
                                                const renderer = new THREE.WebGLRenderer({ alpha: true });
                                                renderer.setSize(window.innerWidth, window.innerHeight);
                                                renderer.shadowMap.enabled = true;
                                                renderer.shadowMap.type = THREE.PCFSoftShadowMap;
                                                document.getElementById('container3D').appendChild(renderer.domElement);
        
                                                const topLight = new THREE.DirectionalLight(0xffffdd, 5);
                                                topLight.position.set(40, 0, 0);
                                                topLight.castShadow = true;
                                                topLight.shadow.camera.left = -50;
                                                topLight.shadow.camera.right = 50;
                                                topLight.shadow.camera.top = 50;
                                                topLight.shadow.camera.bottom = -50;
                                                topLight.shadow.camera.near = 0.1;
                                                topLight.shadow.camera.far = 200;
                                                topLight.shadow.mapSize.width = 2048;
                                                topLight.shadow.mapSize.height = 2048;
                                                scene.add(topLight);
        
                                                const ambientLight = new THREE.AmbientLight(0x333333, 3);
                                                scene.add(ambientLight);
        
                                                controls = new OrbitControls(camera, renderer.domElement);
                                                controls.enableDamping = true;
                                                controls.dampingFactor = 0.05;
                                                controls.zoomSpeed = 0;
                                                controls.maxDistance = 1000;
                                                controls.minDistance = 10;
        
                                                function animate() {
                                                requestAnimationFrame(animate);
        
                                                if (object && objToRender === 'ey') {
                                                object.rotation.y = -3 + (mouseX / window.innerWidth) * 3;
                                                object.rotation.x = -1.2 + (mouseY * 2.5) / window.innerHeight;
                                                }
        
                                                if (object && object.mixer) {
                                                object.mixer.update(0.01);
                                                }
        
                                                controls.update();
                                                renderer.render(scene, camera);
                                                }
        
                                                window.addEventListener('resize', function () {
                                                camera.aspect = window.innerWidth / window.innerHeight;
                                                camera.updateProjectionMatrix();
                                                renderer.setSize(window.innerWidth, window.innerHeight);
                                                });
        
                                                document.onmousemove = (e) => {
                                                mouseX = e.clientX;
                                                mouseY = e.clientY;
                                                };
        
                                                animate();
                                            </script>
                                          </body>
                                        </html>
                                ">
        
                    </iframe>
                </div>
                
            </div>
